package operatiors;

public class MoreCompMath {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int abs = Math.abs(-10);
		
		int min = Math.min(10, 20);
		int max = Math.max(10, 20);
		
		double roundedDown = Math.round(23.445);
		double roundedUp = Math.round(23.545);
		
		double random = Math.random();
		
		double sqrt = Math.sqrt(4);
		
		double pow = Math.pow(2, 2);
		
		System.out.println("abs = "+abs + " min = " + min + " max = " + max 
				+ " roundedDown = " + roundedDown + " roundedUp " +roundedUp 
				+ "\nrandom = " +random + " sqrt = " +sqrt + " pow = " +pow);
		
		
	}

}
